mapper.delete:
DELETE 
  FROM challenge 
 WHERE challenge_no = #{challengeNo}
   AND user_id= #{userId}

mapper.update:
UPDATE challenge 
   SET success = 'Y' 
 WHERE challenge_no = #{challengeNo} 
   AND user_id = #{userId}

UPDATE challenge 
   SET success = 'N' 
 WHERE challenge_no = #{challengeNo} 
   AND user_id = #{userId}
