mapper.select:
select patient_no
from patient
where user_id=#{userId} and hospital_id=#{hospitalId}

mapper.insert:

	select nvl(max(clinic_no), 0)+1
	from clinic



insert into
clinic(
clinic_no
,clinic_symptom
,specificity
,pay_yn
,reserve_no
,perscription_yn
,hospital_id
,patient_no
)

values (
#{clinicNo}
,#{clinicSymptom}
,#{specificity}
,#{payYn}
,#{reserveNo}
,

	 'Y' 
	 #{perscriptionYn} 

,#{hospitalId}
,#{patientNo}
)
