mapper.select:
SELECT c.perscription_yn "perscriptionYn" , r.RESERVE_NO "reserveNo" ,r.reserve_status "reserveStatus" , u.birthday "userBirthday" , u.gender "gender" , u.phone "userPhone" , u.user_name "userName" , u.user_id "userId" , r.reserve_time "reserveTime" , d.doctor_name "doctorName" , r.reserve_kindstatus "reserveKindstatus" FROM reservation r LEFT JOIN clinic c ON c.reserve_no = r.reserve_no JOIN doctor d ON r.doctor_no = d.doctor_no AND r.hospital_id = d.hospital_id JOIN usertable u ON u.user_id = r.user_id WHERE r.hospital_id = #{hospitalId} AND r.reserve_year||r.RESERVE_month||r.reserve_day =
to_char(sysdate, 'YYYYMMDD')
#{date}
AND r.reserve_kindstatus = #{reserveKindstatus}
ORDER BY r.reserve_time, r.reserve_no

SELECT r.RESERVE_NO "reserveNo" ,r.reserve_status "reserveStatus" , u.birthday "userBirthday" , u.gender "gender" , u.phone "userPhone" , u.user_name "userName" , u.user_id "userId" , r.reserve_time "reserveTime" , d.doctor_name "doctorName" , r.reserve_kindstatus "reserveKindstatus" FROM reservation r JOIN doctor d ON r.doctor_no = d.doctor_no AND r.hospital_id = d.hospital_id JOIN usertable u ON u.user_id = r.user_id WHERE r.hospital_id = #{hospitalId} AND r.reserve_year||r.RESERVE_month||r.reserve_day =
to_char(sysdate, 'YYYYMMDD')
#{date}
AND d.doctor_No = #{doctorNo}
AND r.reserve_kindstatus = #{reserveKindstatus}
ORDER BY r.reserve_time, r.reserve_no

