mapper.select:
SELECT * FROM ( SELECT notice_no "noticeNo", title "title", content "content", wdate "wdate", udate "udate", ROWNUM AS rn FROM ( SELECT notice_no, title, content, wdate, udate FROM notice WHERE hospital_id = #{hospitalId}
AND title LIKE '%'|| #{keyword} ||'%'
AND content LIKE '%'|| #{keyword} ||'%'
ORDER BY notice_no DESC )
WHERE ROWNUM <= #{page} * 10
) WHERE rn > (#{page} - 1) * 10

SELECT COUNT(*) FROM notice WHERE hospital_id = #{hospitalId}
AND title LIKE '%'|| #{keyword} ||'%'
AND content LIKE '%'|| #{keyword} ||'%'

mapper.delete:
DELETE FROM notice
 WHERE notice_no = #{noticeNo}
   AND hospital_id = #{hospitalId}

mapper.insert:
