mapper.insert:

INSERT INTO usertable (user_id
					, user_pw
					, user_name
					, phone
					, birthday
					, gender
					, identification)
VALUES (#{userId}
		, #{userPw}
		, #{userName}
		, #{phone}
		, #{birthday}
		, #{gender}
		, #{identification})


