mapper.select:
SELECT COUNT(phone)
FROM user_login_view
WHERE phone = #{phone}

SELECT COUNT(user_id)
FROM user_login_view
WHERE user_id = #{id}

SELECT
user_id
FROM user_detail_view
WHERE user_name = #{userName}
AND phone =
#{phone}

choose.when:
UPDATE usertable
SET user_pw = #{userPw}
WHERE user_id = #{userId}

UPDATE hospital
SET hospital_pw = #{userPw}
WHERE hospital_id = #{userId}

UPDATE pharmacy
SET pharmacy_pw = #{userPw}
WHERE pharmacy_id = #{userId}

mapper.update:
UPDATE usertable
SET  user_pw = #{userPw}
	,phone = #{phone}
WHERE user_id = #{userId}

UPDATE hospital
SET hospital_pw = #{hospitalPw}
  , phone = #{phone}
  , hospital_img = #{hospitalImg}
  , director_phone = #{directorPhone}
  , holiday = #{holiday}
  , opentime = #{opentime}
  , closetime = #{closetime}
WHERE hospital_id = #{hospitalId}

UPDATE pharmacy
SET pharmacy_pw = #{pharmacyPw}
  , pharmacy_phone = #{pharmacyPhone}
  , pharmacy_fax = #{pharmacyFax}
  , pharmacy_img = #{pharmacyImg}
  , ceo_phone = #{ceoPhone}
  , holiday = #{holiday}
  , opentime = #{opentime}
  , closetime = #{closetime}
WHERE pharmacy_id = #{pharmacyId}

