mapper.update:
UPDATE pharmacy_select 
   SET rejection = #{rejection} 
 WHERE clinic_no = #{clinicNo}
   AND pharmacy_id = #{pharmacyId}

UPDATE pharmacy_select
   SET produce_status = 'f2'
 WHERE pharmacy_selectno = #{pharmacySelectno}

mapper.select:
SELECT * FROM ( SELECT z.*,ROWNUM AS rn FROM (
SELECT c.patient_no "patientNo", c.clinic_no "clinicNo", c.hospital_id "hospitalId", c.reserve_no "reserveNo", c.perscription_yn "perscriptionYn", a.user_id "patientUserId", u.user_name "userName", u.birthday "userBirthday", u.phone "userPhone", h.hospital_name "hospitalName", h.phone "hospitalPhone", d.doctor_name "doctorName", s.print_status "printStatus", c.clinic_date "perscriptionDate", s.send_status "sendStatus", s.rejection "rejection", s.pharmacy_selectno "pharmacySelectno", p.pharmacy_name "pharmacyName" FROM clinic c LEFT JOIN patient a ON (c.patient_no = a.patient_no AND c.hospital_id = a.hospital_id) JOIN usertable u ON (a.user_id = u.user_id AND c.hospital_id = a.hospital_id) JOIN hospital h ON c.hospital_id = h.hospital_id JOIN reservation r ON c.reserve_no = r.reserve_no JOIN doctor d ON r.doctor_no = d.doctor_no JOIN pharmacy_select s ON c.clinic_no = s.clinic_no JOIN pharmacy p ON s.pharmacy_id = p.pharmacy_id WHERE s.print_status = 'd1' AND s.send_status = 'Y' AND to_char(c.clinic_date, 'yyyy-MM-dd') = #{date} AND s.rejection IS NULL AND s.produce_status = 'f1' AND s.pharmacy_id = #{pharmacyId}
ORDER BY c.clinic_no ) z WHERE ROWNUM <= #{page} * 5 ) x WHERE rn > (#{page} - 1) * 5

mapper.update:
UPDATE pharmacy_select 
